const LightsOutPuzzlePreview = props => {
  const {
    initialStatusVector,
    targetStatusVectors,
    modulus,
    toggleMatrix,
    layouts
  } = props;
  const size = Math.sqrt(initialStatusVector.length);
  const ref = useRef();
  useEffect(() => {
    const puzzle = new LightsOut({
      canvas: ref.current,
      initialStatusVector,
      targetStatusVectors,
      modulus,
      size,
      toggle_matrix: toggleMatrix,
      layouts
    });
    puzzle.render();
  }, [initialStatusVector, targetStatusVectors, modulus, toggleMatrix, layouts, size]);
  return /*#__PURE__*/React.createElement("div", {
    className: styles['puzzle-preview'],
    style: {
      width: layouts.width
    }
  }, /*#__PURE__*/React.createElement("canvas", {
    width: layouts.width,
    height: layouts.width,
    ref: ref
  }));
};

